    public class AuctionsSiteMailSender
    {
        public void SendNotInitiatedDeleteMail(Auction auction, User buyer)
        {
            var mailSender = new MailSender.MailSender();
            var mailInfo = new MailInfo
            {
                ReceiverAddress = auction.User.Email,
                ReceiverName = auction.User.Name,
                Subject = "Your auction has ended!",
                Body = NotInitiatedSellerMailBody(auction)
            };
            mailSender.SendMail(mailInfo);

            if (buyer == null) return;

            mailInfo.ReceiverAddress = buyer.Email;
            mailInfo.ReceiverName = buyer.Name;
            mailInfo.Subject = "You won an auction!";
            mailInfo.Body = NotInitiatedBuyerMailBody(auction);
            mailSender.SendMail(mailInfo);
        }

        public void SendInitiatedDeleteMail(Auction auction, User buyer)
        {
            var mailSender = new MailSender.MailSender();

            var mailInfo = new MailInfo
            {
                ReceiverAddress = auction.User.Email,
                ReceiverName = auction.User.Name,
                Subject = "Your auction has been canceled",
                Body = InitiatedSellerMailBody(auction)
            };
            mailSender.SendMail(mailInfo);

            if (buyer == null) return;

            mailInfo.ReceiverAddress = buyer.Email;
            mailInfo.ReceiverName = buyer.Name;
            mailInfo.Subject = "Auction has been canceled";
            mailInfo.Body = InitiatedBuyerMailBody(auction);
            mailSender.SendMail(mailInfo);
        }

        public void SendNewUserMail(User user)
        {
            var mailSender = new MailSender.MailSender();

            var mailInfo = new MailInfo
            {
                ReceiverAddress = user.Email,
                ReceiverName = user.Name,
                Subject = "Wellcome to NgAuctions",
                Body = NewUserMailBody(user)
            };
            mailSender.SendMail(mailInfo);
        }

