        public void SendHeigherBidMail(Auction auction, User bidder)
        {
            var mailSender = new MailSender.MailSender();

            var mailInfo = new MailInfo
            {
                ReceiverAddress = bidder.Email,
                ReceiverName = bidder.Name,
                Subject = "Someone placed heigher bid than you!",
                Body = HeigherBidBody(auction, bidder)
            };
            mailSender.SendMail(mailInfo);
        }

